drop table if exists onlineddl_test;
create table onlineddl_test (
  id int auto_increment,
  i int not null,
  updated tinyint not null default 0,
  j json,
  primary key(id)
) auto_increment=1;

drop event if exists onlineddl_test;
delimiter ;;
